import React from 'react';

class Signup extends React.Component {
    state = {
        username: "",
        email: "",
        password : "",
        passwordConfirmation : ""
    }

    handleChange = event => {
        const { name, value } = event.target;
        this.setState({ [name] : value })
    }

    render(){
       const { username, email, password, passwordConfirmation } = this.state;

        return(
            <div className="App">
                <h2 className="App">Signup</h2>
                <form className="form">
                    <input type="text" name="username" placeholder="Username" value={username} onChange={this.handleChange}/> <br/>
                    <input type="email" name="email" placeholder="Email"/> <br/>
                    <input type="password" name="password" placeholder="Password" onChange={this.handleChange}/> <br/>
                    <input type="password" name="passwordConfirmation" placeholder="Confirm password" /> <br/>

                    <button type="submit" className="button-primary">
                        Submit 
                    </button>
                </form>
            </div>
        )
    }
}

export default Signup;
